import React, { useEffect, useState } from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';

const backendUrl = 'http://localhost:5000';

function Login() {
  return (
    <div style={{ textAlign: 'center', marginTop: '5rem' }}>
      <h2>Sign in to Image Search App</h2>
      <a href={`${backendUrl}/auth/google`}>
        <button style={{ fontSize: '1rem', padding: '0.75rem 2rem', marginTop: '2rem' }}>
          Sign in with Google
        </button>
      </a>
    </div>
  );
}

function Home({ user, onLogout }) {
  return (
    <div style={{ textAlign: 'center', marginTop: '5rem' }}>
      <h2>Welcome, {user.displayName || 'User'}!</h2>
      {user.photo && <img src={user.photo} alt="Profile" style={{ borderRadius: '50%', width: 100, height: 100, margin: '20px 0' }} />}
      <p>You are logged in.</p>
      <button onClick={onLogout} style={{ fontSize: '1rem', padding: '0.5rem 1.5rem', marginTop: '2rem' }}>Logout</button>
    </div>
  );
}

function App() {
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetch(`${backendUrl}/auth/me`, {
      method: 'GET',
      credentials: 'include', // allow cookies/session
    })
      .then(res => res.ok ? res.json() : null)
      .then(data => {
        setUser(data && data._id ? data : null);
        setLoading(false);
      })
      .catch(() => setLoading(false));
  }, []);

  const handleLogout = async () => {
    await fetch(`${backendUrl}/logout`, {
      method: 'GET',
      credentials: 'include',
    });
    setUser(null);
    window.location.href = '/login';
  };

  if (loading) return <div style={{ textAlign: 'center', marginTop: '5rem' }}>Loading...</div>;

  return (
    <Router>
      <Routes>
        <Route path="/login" element={user ? <Navigate to="/" /> : <Login />} />
        <Route path="/" element={user ? <Home user={user} onLogout={handleLogout} /> : <Navigate to="/login" />} />
      </Routes>
    </Router>
  );
}

export default App;
