import React, { useEffect, useState } from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import styles from './App.module.css';
import loginStyles from './Login.module.css';

const backendUrl = 'http://localhost:5000';

function TravelistaLogin() {
  return (
    <div className={loginStyles.loginPageContainer}>
      {/* Branding Side */}
      <div className={loginStyles.brandingSide}>
        <div className={loginStyles.brandingContent}>
          <h1 className={loginStyles.travelistaTitle}>Travelista Tours</h1>
          <p className={loginStyles.tagline}>Travel is the only purchase that enriches you in ways beyond material wealth</p>
        </div>
      </div>
      {/* Login Side */}
      <div className={loginStyles.loginSide}>
        <div className={loginStyles.loginCard}>
          <div className={loginStyles.planeAnimation}>✈️</div>
          <h2 className={loginStyles.welcomeTitle}>Welcome</h2>
          <p className={loginStyles.loginSubtitle}>Login with your account</p>
          <form className={loginStyles.loginForm} autoComplete="off" onSubmit={e => e.preventDefault()}>
            <div className={loginStyles.inputGroup}>
              <label htmlFor="email">Email</label>
              <i className="fas fa-envelope icon" />
              <input type="email" id="email" placeholder="your@email.com" disabled />
            </div>
            <div className={loginStyles.inputGroup}>
              <label htmlFor="password">Password</label>
              <i className="fas fa-lock icon" />
              <input type="password" id="password" placeholder="••••••••••" disabled />
            </div>
            <a href="#" className={loginStyles.forgotPassword}>Forgot your password?</a>
            <button type="submit" className={loginStyles.loginButton} disabled>LOGIN</button>
          </form>
          <div className={loginStyles.divider}>OR</div>
          <div className={loginStyles.socialLogin}>
            <a href={`${backendUrl}/auth/google`} title="Google login">
              <button className={`${loginStyles.socialButton} ${loginStyles.google}`} type="button">
                <i className="fab fa-google"></i>
              </button>
            </a>
            <a href={`${backendUrl}/auth/facebook`} title="Facebook login">
              <button className={`${loginStyles.socialButton} ${loginStyles.facebook}`} type="button">
                <i className="fab fa-facebook-f"></i>
              </button>
            </a>
            <a href={`${backendUrl}/auth/github`} title="GitHub login">
              <button className={`${loginStyles.socialButton} ${loginStyles.apple}`} type="button">
                <i className="fab fa-github"></i>
              </button>
            </a>
          </div>
          <p className={loginStyles.registerText}>
            Don't have account? <span className={loginStyles.registerLink} style={{ cursor: 'not-allowed', color: '#ccc' }}>Register Now</span>
          </p>
          <div className={loginStyles.citySilhouette}></div>
        </div>
      </div>
    </div>
  );
}

function Home({ user, onLogout }) {
  const [term, setTerm] = useState('');
  const [results, setResults] = useState(null);
  const [loadingSearch, setLoadingSearch] = useState(false);
  const [selected, setSelected] = useState(new Set());
  const [topSearches, setTopSearches] = useState([]);
  const [history, setHistory] = useState([]);

  const fetchHistory = async () => {
    try {
      const res = await fetch(`${backendUrl}/api/history`, { credentials: 'include' });
      if (res.ok) {
        const data = await res.json();
        setHistory(data);
      }
    } catch (err) {
      console.error('Failed to fetch history:', err);
    }
  };

  useEffect(() => {
    const fetchTopSearches = async () => {
      try {
        const res = await fetch(`${backendUrl}/api/top-searches`, { credentials: 'include' });
        if (res.ok) {
          const data = await res.json();
          setTopSearches(data);
        }
      } catch (err) {
        console.error('Failed to fetch top searches:', err);
      }
    };
    fetchTopSearches();
    fetchHistory();
  }, []);

  const handleSearch = async (e, searchTerm = term) => {
    if (e) e.preventDefault();
    if (!searchTerm) return;
    setLoadingSearch(true);
    try {
      const res = await fetch(`${backendUrl}/api/search`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        credentials: 'include',
        body: JSON.stringify({ term: searchTerm }),
      });
      const data = await res.json();
      setResults(data);
      fetchHistory(); // Refresh history after a new search
    } catch (err) {
      console.error('Search failed:', err);
    } finally {
      setLoadingSearch(false);
    }
  };

  const handleTopSearchClick = (topTerm) => {
    setTerm(topTerm);
    handleSearch(null, topTerm);
  };

  const toggleSelect = (id) => {
    setSelected(prev => {
      const newSet = new Set(prev);
      if (newSet.has(id)) newSet.delete(id);
      else newSet.add(id);
      return newSet;
    });
  };

  return (
    <div className={styles.container}>
      <header className={styles.header}>
        <div className={styles.profile}>
          {user.photo && <img src={user.photo} alt="Profile" className={styles.avatar} />}
          <span>Welcome, {user.displayName}!</span>
        </div>
        <button className={styles.logout} onClick={onLogout}>Logout</button>
      </header>

      <div className={styles.mainLayout}>
        <main className={styles.main}>
          <div className={styles.topSearches}>
            <strong style={{ marginRight: '10px' }}>Top Searches:</strong>
            {topSearches.length > 0 ? topSearches.map((s, index) => (
              <button
                key={index}
                className={styles.topButton}
                onClick={() => handleTopSearchClick(s.term)}
              >
                {s.term}
              </button>
            )) : <span>No top searches yet.</span>}
          </div>

          <form onSubmit={handleSearch} className={styles.searchForm}>
            <input
              type="text"
              value={term}
              onChange={(e) => setTerm(e.target.value)}
              placeholder="Search for images..."
              className={styles.input}
            />
            <button type="submit" className={styles.search}>Search</button>
          </form>

          {loadingSearch && <p style={{ textAlign: 'center' }}>Loading results...</p>}

          {results && (
            <div>
              <div className={styles.statusBar}>
                <span>You searched for "{results.term}" &mdash; {results.total} results found.</span>
                <span className={styles.selectedCount}>Selected: {selected.size} images</span>
              </div>
              <div className={styles.grid}>
                {results.results.map(img => (
                  <div
                    key={img.id}
                    className={styles.imgWrapper}
                    onClick={() => toggleSelect(img.id)}
                  >
                    <img src={img.urls.small} alt={img.alt_description} className={styles.unsplImg} />
                    <input
                      type="checkbox"
                      checked={selected.has(img.id)}
                      readOnly
                      className={styles.check}
                    />
                  </div>
                ))}
              </div>
            </div>
          )}
        </main>

        <aside className={styles.sidebar}>
          <h3>Search History</h3>
          {history.length > 0 ? (
            <ul style={{ listStyle: 'none', padding: 0 }}>
              {history.map((item, index) => (
                <li key={index} style={{ marginBottom: '10px' }}>
                  <strong>{item.term}</strong><br />
                  <small>{new Date(item.timestamp).toLocaleString()}</small>
                </li>
              ))}
            </ul>
          ) : (
            <p>No search history yet.</p>
          )}
        </aside>
      </div>
    </div>
  );
}

function App() {
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetch(`${backendUrl}/auth/me`, {
      method: 'GET',
      credentials: 'include',
    })
      .then(res => res.ok ? res.json() : null)
      .then(data => {
        setUser(data && data._id ? data : null);
        setLoading(false);
      })
      .catch(() => setLoading(false));
  }, []);

  const handleLogout = async () => {
    await fetch(`${backendUrl}/logout`, {
      method: 'GET',
      credentials: 'include',
    });
    setUser(null);
    window.location.href = '/login';
  };

  if (loading) return <div style={{textAlign:'center',marginTop:'5rem'}}>Loading...</div>;

  return (
    <Router>
      <Routes>
        <Route path="/login" element={user ? <Navigate to="/" /> : <TravelistaLogin />} />
        <Route path="/" element={user ? <Home user={user} onLogout={handleLogout} /> : <Navigate to="/login" />} />
      </Routes>
    </Router>
  );
}

export default App;
