import React, { useEffect, useState } from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';

const backendUrl = 'http://localhost:5000';

function Login() {
  return (
    <div style={{ textAlign: 'center', marginTop: '5rem' }}>
      <h2>Sign in to Image Search App</h2>
      <a href={`${backendUrl}/auth/google`}>
        <button style={{ fontSize: '1rem', padding: '0.75rem 2rem', marginTop: '2rem' }}>
          Sign in with Google
        </button>
      </a>
    </div>
  );
}

function Home({ user, onLogout }) {
    const [term, setTerm] = useState('');
    const [results, setResults] = useState(null);
    const [loadingSearch, setLoadingSearch] = useState(false);
    const [selected, setSelected] = useState(new Set());

    const handleSearch = async (e) => {
      e.preventDefault();
      if (!term) return;
      setLoadingSearch(true);
      try {
        const res = await fetch(`${backendUrl}/api/search`, {
          method: 'POST',
          headers: { 'Content-Type': 'application/json' },
          credentials: 'include',
          body: JSON.stringify({ term }),
        });
        const data = await res.json();
        setResults(data);
      } catch (err) {
        console.error('Search failed:', err);
      } finally {
        setLoadingSearch(false);
      }
    };

    const toggleSelect = (id) => {
      setSelected(prev => {
        const newSet = new Set(prev);
        if (newSet.has(id)) newSet.delete(id);
        else newSet.add(id);
        return newSet;
      });
    };

    return (
      <div style={{ maxWidth: 1200, margin: '0 auto', padding: '20px' }}>
        <header style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: '2rem' }}>
          <div style={{ display: 'flex', alignItems: 'center' }}>
            {user.photo && <img src={user.photo} alt="Profile" style={{ borderRadius: '50%', width: 50, height: 50, marginRight: 15 }} />}
            <span>Welcome, {user.displayName}!</span>
          </div>
          <button onClick={onLogout}>Logout</button>
        </header>

        <main>
          <form onSubmit={handleSearch} style={{ textAlign: 'center', marginBottom: '2rem' }}>
            <input
              type="text"
              value={term}
              onChange={(e) => setTerm(e.target.value)}
              placeholder="Search for images..."
              style={{ fontSize: '1rem', padding: '10px', width: '300px', marginRight: '10px' }}
            />
            <button type="submit" style={{ fontSize: '1rem', padding: '10px 20px' }}>Search</button>
          </form>

          {loadingSearch && <p style={{ textAlign: 'center' }}>Loading results...</p>}

          {results && (
            <div>
              <div style={{ marginBottom: '1rem', textAlign: 'center', fontSize: '1.2rem' }}>
                <span>You searched for "{results.term}" &mdash; {results.total} results found.</span>
                <span style={{ marginLeft: '2rem', fontWeight: 'bold' }}>Selected: {selected.size} images</span>
              </div>
              <div style={{ display: 'grid', gridTemplateColumns: 'repeat(4, 1fr)', gap: '15px' }}>
                {results.results.map(img => (
                  <div key={img.id} style={{ position: 'relative', cursor: 'pointer' }} onClick={() => toggleSelect(img.id)}>
                    <img src={img.urls.small} alt={img.alt_description} style={{ width: '100%', height: '200px', objectFit: 'cover', display: 'block' }} />
                    <input
                      type="checkbox"
                      checked={selected.has(img.id)}
                      readOnly
                      style={{ position: 'absolute', top: 10, right: 10, width: 20, height: 20 }}
                    />
                  </div>
                ))}
              </div>
            </div>
          )}
        </main>
      </div>
    );
  }

function App() {
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetch(`${backendUrl}/auth/me`, {
      method: 'GET',
      credentials: 'include',
    })
      .then(res => res.ok ? res.json() : null)
      .then(data => {
        setUser(data && data._id ? data : null);
        setLoading(false);
      })
      .catch(() => setLoading(false));
  }, []);

  const handleLogout = async () => {
    await fetch(`${backendUrl}/logout`, {
      method: 'GET',
      credentials: 'include',
    });
    setUser(null);
    window.location.href = '/login';
  };

  if (loading) return <div style={{textAlign:'center',marginTop:'5rem'}}>Loading...</div>;

  return (
    <Router>
      <Routes>
        <Route path="/login" element={user ? <Navigate to="/" /> : <Login />} />
        <Route path="/" element={user ? <Home user={user} onLogout={handleLogout} /> : <Navigate to="/login" />} />
      </Routes>
    </Router>
  );
}

export default App;
